				else if(tileMap[i, j] == 5){
					position = new Vector2((water.renderer.bounds.size.x * mapHeight) - i*water.renderer.bounds.size.x, j*water.renderer.bounds.size.y);
					rotatingBlock.GetComponent<BlocksWillFall>().fallTime = (j * .1f) + TimeTillFall;
					Instantiate(rotatingBlock, new Vector3(position.y, position.x + water.renderer.bounds.size.x/2, 0), Quaternion.identity);
				} 
				else if(tileMap[i, j] == 6){
					position = new Vector2((water.renderer.bounds.size.x * mapHeight) - i*water.renderer.bounds.size.x, j*water.renderer.bounds.size.y + (water.renderer.bounds.size.y));
					movingBlock.GetComponent<BlocksWillFall>().fallTime = (j * .1f) + TimeTillFall;
					Instantiate(movingBlock, new Vector3(position.y + water.renderer.bounds.size.x/2, position.x, 0), Quaternion.identity);
				} 
				else if(tileMap[i, j] == 7){
					position = new Vector2((water.renderer.bounds.size.x * mapHeight) - i*water.renderer.bounds.size.x, j*water.renderer.bounds.size.y);
					speedBlock.GetComponent<BlocksWillFall>().fallTime = (j * .1f) + TimeTillFall;
					Instantiate(speedBlock, new Vector3(position.y, position.x, 0), Quaternion.identity);
				} 
				else if(tileMap[i, j] == 8){
					position = new Vector2((respawn.renderer.bounds.size.x * mapHeight) - i*respawn.renderer.bounds.size.x, j*respawn.renderer.bounds.size.y);
					respawn.GetComponent<BlocksWillFall>().fallTime = (j * .1f) + TimeTillFall;
					Instantiate(respawn, new Vector3(position.y, position.x, 0), Quaternion.identity);
				}
				else if(tileMap[i, j] == 9){
					position = new Vector2((grass.renderer.bounds.size.x * mapHeight) - i*grass.renderer.bounds.size.x, j*grass.renderer.bounds.size.y);
					Debug.Log (position.y);
					player.transform.position = position;
					//Instantiate(playerSpawn, new Vector3(position.y, position.x, 0), Quaternion.identity);
				}
				else if(tileMap[i, j] == 12){
					position = new Vector2((respawn.renderer.bounds.size.x * mapHeight) - i*respawn.renderer.bounds.size.x, j*respawn.renderer.bounds.size.y);
					finish.GetComponent<BlocksWillFall>().fallTime = (j * .1f) + TimeTillFall;
					Instantiate(finish, new Vector3(position.y, position.x, 0), Quaternion.identity);
				}
			}
		}
	}
